nowGroup=Authentication.findMaxLevelGroupByUserId(processInfo.getInitiator(),"role");
nowLevelNum=nowGroup.getLevelNum();
oldGroup=Authentication.findGroupByGroupIdAndType("bumenjingli","role")
oldLevelNum=oldGroup.getLevelNum();
if(nowLevelNum<oldLevelNum){
	return 	true;
}
else{
	return 	false;
