mapper.delete:
DELETE FROM
	t_sys_event
WHERE id IN (

	${_parameter}

)

mapper.select:
SELECT t.* FROM t_sys_event t
AND t.userName LIKE '%${userName}%'
ORDER BY t.id DESC LIMIT #{startRow}, #{pageSize}

mapper.delete:
DELETE FROM
	t_sys_exceptions
WHERE id IN (

	${_parameter}

)

mapper.select:
SELECT t.* FROM t_sys_exceptions t
AND t.className LIKE '%${className}%'
ORDER BY t.id DESC LIMIT #{startRow}, #{pageSize}
