mapper.delete:
DELETE FROM
	t_sys_privilege
WHERE id IN (

	${_parameter}

)

mapper.select:
SELECT t.* FROM t_sys_privilege t
AND t.privilegeName LIKE '%${privilege.privilegeName}%'
ORDER BY t.id DESC LIMIT #{startRow}, #{pageSize}

mapper.delete:
DELETE FROM 
	t_sys_privilege_menu
WHERE privilegeId IN (

	${_parameter}

)

mapper.select:
SELECT DISTINCT p.* FROM t_sys_role r, t_sys_privilege p, t_sys_role_privilege rp, t_sys_user u, t_sys_user_role ur WHERE r.id = rp.roleId AND p.id = rp.privilegeId AND u.id = ur.userId AND r.id = ur.roleId AND p.status = '启用'
AND u.id = #{id}
AND u.userName = #{userName}
